import React, { useEffect, useState } from 'react';
import './App.css';
import Axios from 'axios';


function App() {

  const [categoryList, setCategoryList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/get-categories").then((res) => {
      setCategoryList(res.data)
      console.log(res.data);
    })
  }, [])


  return (
    <div className="App">
      <br /><br />
      <p className='m-0 display-4 fw-bold text-center'>Daraz Category Dropdown and Search</p>
      <br /><br />
      <div className="container">
        <div className="row">
          <div className="col-12 col-sm-9.col-md-4 col-lg-4 mx-auto">
            <div className="dropdown">
              <button className="btn btn-light border dropdown-toggle w-100" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                Categories
              </button>
              <ul className="dropdown-menu w-100 list">
                {categoryList.map((val, key) => {
                  return <li className='dropdown-submenu'>
                    <a tabIndex={-1} className="dropdown-item" key={key}>{val.name}</a>
                      <ul className='dropdown-menu'>
                        {val.children.map((submenu, nkey) => {
                          return <li><a className='dropdown-item' key={nkey}>{submenu.name}</a></li>
                        })}

                      </ul>
                    </li>
                })}
              </ul>
            </div>
            <br />

          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
