import React, { useEffect, useState } from 'react';
import './App.css';
import Axios from 'axios';


function App() {

  const [categoryList, setCategoryList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/get-categories").then((res) => {
      setCategoryList(res.data)
      console.log(res.data);
    })
  }, [])


  return (
    <div className="App">
      <br /><br />
      <p className='m-0 display-4 fw-bold text-center'>Daraz Category Dropdown and Search</p>
      <br /><br />
      <div className="container">
        <div className="row">
          <div className="col-12 col-sm-9.col-md-4 col-lg-4 mx-auto">
            <div className="dropdown">
              <button className="btn btn-light border dropdown-toggle w-100" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                Dropdown button
              </button>
              <ul className="dropdown-menu w-100 list">
                {categoryList.map((val, key) => {
                  return <li><a className="dropdown-item">{val.name}</a></li>
                })}
              </ul>
            </div>
            <br />
            <div class="dropdown">
              <button class="btn btn-default dropdown-toggle" type="button" data-toggle="dropdown">Tutorials
                <span class="caret"></span></button>
              <ul class="dropdown-menu">
                <li><a tabindex="-1" href="#">HTML</a></li>
                <li><a tabindex="-1" href="#">CSS</a></li>
                <li class="dropdown-submenu">
                  <a class="test" tabindex="-1" href="#">New dropdown <span class="caret"></span></a>
                  <ul class="dropdown-menu">
                    <li><a tabindex="-1" href="#">2nd level dropdown</a></li>
                    <li><a tabindex="-1" href="#">2nd level dropdown</a></li>
                    <li class="dropdown-submenu">
                      <a class="test" href="#">Another dropdown <span class="caret"></span></a>
                      <ul class="dropdown-menu">
                        <li><a href="#">3rd level dropdown</a></li>
                        <li><a href="#">3rd level dropdown</a></li>
                      </ul>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
