import React, { useEffect, useState } from 'react';
import './App.css';
import Axios from 'axios';
import CategoryList from './CategoryList';


function App() {

  const [categoryList, setCategoryList] = useState([]);
  const [inputText, setInputText] = useState("");

  let inputHandler = (e) => {
    var lowerCase = e.target.value.toLowerCase();
    setInputText(lowerCase);
    console.log(inputText)
  };


  useEffect(() => {
    Axios.get("http://localhost:3001/get-categories").then((res) => {
      setCategoryList(res.data);
    })
  }, [])


  return (
    <div className="App">
      <br />
      <p className='m-0 display-6 fw-bold text-center'>Daraz Category Dropdown and Search</p>
      <br />
      <div className="container">
        <div className="row">
          <div className="col-12 col-sm-9.col-md-4 col-lg-4 mx-auto">
            <ul className="dropdown">
              <li>
                <div>
                  <input className='form-control fs-7' onChange={inputHandler} type={'text'} placeholder="Category Name"></input>
                </div>
                <CategoryList input={inputText}></CategoryList>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
